mapper.update:
update st_sheep
SET
    sheep_status = #{sheepStatus,jdbcType=INTEGER},
    operate_uid = #{operateUid,jdbcType=INTEGER},
    modify_time = #{modifyTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=BIGINT}

update st_sheep
SET
    sheep_status = #{sheepStatus,jdbcType=INTEGER},
    operate_uid = null,
    modify_time = #{modifyTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select sheep.id,sheep.device_id, sheep.sheep_nickname, sheep.gender, sheep.sheep_img , sheep.birth_day
from st_sheep sheep
where sheep.id = #{sid} and sheep.user_id = #{uid}
