mapper.select:
select
sheep.sheep_nickname, sheep.user_id, sheep.gender, sheep.sheep_img, sheep.birth_day, sheep.claim_time, sheep.sheep_breeds_id, de_info.step
from (fictitious_sheep sheep)
left join(
  select
  sum(info.step) step, info.device_id
  from fictitious_device_info info
  where info.data_time between #{start} and #{end}
) de_info on de_info.device_id = sheep.device_id
where sheep.id = #{id}

mapper.update:
update fictitious_sheep
SET
sheep_status = #{sheepStatus,jdbcType=INTEGER},
operate_uid = #{operateUid,jdbcType=INTEGER},
modify_time = #{modifyTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=BIGINT}

update fictitious_sheep
SET
sheep_status = #{sheepStatus,jdbcType=INTEGER},
operate_uid = null,
modify_time = #{modifyTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=BIGINT}
